import React from 'react';

export default function Atividade() {
    return (
        <div
            key={ativ.id}
            className={
                'card mb-2 shadow-sm border-' + prioridadeStyle(ativ.prioridade)
            }
        >
            <div className='card-body'>
                <div className='d-flex justify-content-between'>
                    <h5 className='card-title'>
                        <span className='badge rounded-pill text-bg-secondary me-1'>
                            {ativ.id}
                        </span>
                        - {ativ.titulo}
                    </h5>
                    <h6>
                        Prioridade:
                        <span
                            className={
                                'ms-1 text-' + prioridadeStyle(ativ.prioridade)
                            }
                        >
                            <i
                                className={
                                    'me-1 far fa-' +
                                    prioridadeStyle(ativ.prioridade, true)
                                }
                            ></i>
                            {prioridadeLabel(ativ.prioridade)}
                        </span>
                    </h6>
                </div>
                <p className='card-text'>{ativ.descricao}</p>
                <div className='d-flex justify-content-end pt-2 m-0 border-top'>
                    <button className='btn btn-sm btn-outline-primary me-2'>
                        <i className='fas fa-pen me-2'></i>
                        Editar
                    </button>
                    <button
                        className='btn btn-sm btn-outline-danger'
                        onClick={() => deletarAtividade(ativ.id)}
                    >
                        <i className='fas fa-trash me-2'></i>
                        Deletar
                    </button>
                </div>
            </div>
        </div>
    );
}
